import React from 'react'
import './SearchItem.css'
import axios from 'axios'
import YoutubeAudio from './YoutubeAudio'
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch,
  } from 'react-router-dom'
import ReadRoom from './ReadRoom';

class SearchItem extends React.Component {


    render(){
        {/* 
        props:
        cover,
        title,
        uploadedBy(channel),
    */}

    var ifrm = `<p style="font-size:13px;padding:10px;border-radius:5px;background-color:black;color:white;border:1px solid black;"><span style="color:rgb(52, 201, 52)"> Playing: </span>` + this.props.title + `</p>`+
    `<iframe id="` + `embed${this.props.id}` +`" style="width:100%;"`+
     `src="` + `https://www.youtube.com/embed/${this.props.id}?vq=small&cc_lang_pref=an&cc_load_policy=1` + `" `+
     `frameborder='0' `+
     `allow='accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture'`+
     `allowfullscreen></iframe>`

        return(
            <div className="container-fluid" style={{margin:'0px'}}>
            <a style={{textDecoration:'none', color:'black', margin:'0px', padding:'0px'}} id={this.props.id} className="container" href={`#${this.props.cover}`}>
            <div className="row" onClick={()=>{
               document.getElementById(`${this.props.cover}`).style.display = 'block';
               document.getElementById(`view${this.props.id}`).innerHTML= ifrm;

            }} id="searchitem" style={{backgroundColor:'black', color:'white',borderRadius:'5px', border: '0px inset gold', padding:'10px', marginBottom:'10px'}}>
                <div className="col-sm-2"><img src={this.props.cover} style={{width:'50%', backgroundColor:'white', border:'0px solid black', borderRadius:'5px',padding:'5px'}}></img></div>
                <div className="col-sm-6"><span style={{color:'rgb(52, 201, 52)'}}>Title:  </span>{this.props.title}</div>
                <div className="col-sm-1"></div>
                <div className="col-sm-3"><span style={{color:'rgb(52, 201, 52)'}}>From: </span>{this.props.uploadedBy}</div>
            </div>
            </a>

            <div id={`${this.props.cover}`} style={{display:'none'}}>
                <a href={`#${this.props.id}`}><button id="closebook" style={{border:'0px'}} onClick={()=>{
                    document.getElementById(`view${this.props.id}`).innerHTML = 'Loading ...';
                    document.getElementById(`${this.props.cover}`).style.display = 'none';

                }}>Close audiobook</button></a>
            <ReadRoom cover={this.props.cover} title={this.props.title} id={this.props.id} />
            </div>


               </div>


        )
    }

}
export default SearchItem;
