        return(
            <div className='row' style={{height:'100vh', padding:'10px'}}>



                <div className="col-sm-3" id={`view${this.props.id}`}>
<br/>            






                </div>


                <div className="col-sm-6" id="notes">
                    <div className="row" align="center">
                        <div className="col-sm-12">Notes will be saved as you write</div></div>
                    <textarea id="notetextarea" style={{padding:'10px' , fontSize:'13px',backgroundColor:'black', width:'100%', color:'white',height:'50%'}} 
                    onChange={()=>{
                        //save notes in the data base
                    }}
                    >{this.props.id}</textarea>

                </div>


                <div className="col-sm-3" id="dictionary"></div>
            </div>
        )
    }
}

export default ReadRoom;
