import React from 'react'
import TopNav from './TopNav'
import './PlayField.css'
{/*
<TopNav>
<3 buttons yt own mountnPlay> onClick-> style.display = none/block >
< for each of the three buttons -> a seperate component > 
*/}

class PlayField extends React.Component {
    render(){
        return(
            <div className="container-fluid">
                <TopNav/>
                <div className="row" style={{
                    padding:'10px', 
                    backgroundColor:'transparent', 
                    borderBottom:'1px solid black', 
                    borderStartEndRadius:'10px'
                    }}>

                    <div className="col-sm-12">
                    <table>
                        <tr>
                            <td style={{paddingRight:'15px'}}>
                            <button id="yt" style={{

                        color:'inherit',
                        backgroundColor:'transparent',
                        borderRadius:'5px',
                        border:'1px solid black',

                        }}  onClick={(e)=>{
                            e.target.style.color='white';
                            e.target.style.backgroundColor='black';

                            document.getElementById("olbtn").style.color='black';
                            document.getElementById("olbtn").style.backgroundColor='white';
                            e.target.style.color='white';
                            e.target.style.backgroundColor='black';
                            /*to toggle*/
                        }}> Youtube Audio Books</button>
                            </td>

                            <td style={{paddingRight:'15px'}}>
                            <button style={{

                        color:'black',
                        backgroundColor:'transparent',
                        borderRadius:'5px',
                        border:'1px solid black',

                        }}>Our Library</button>
                            </td>

                            <td style={{paddingRight:'15px'}}>
                            <button style={{

                        color:'inherit',
                        backgroundColor:'transparent',
                        borderRadius:'5px',
                        border:'1px solid black',

                        }}> Mount and Play</button>
                            </td>
                        </tr>
                    </table>
                    </div>




                    </div>
                </div>

        )
    }
}

export default PlayField;
