import React, { Component } from 'react'
import './ReadRoom.css'
import axios from 'axios'

class ReadRoom extends React.Component {

    state={
        dictioanryRes: [],
        showDicRes: false,
    }

    render(){
        return(
            <div className='col-sm-12' style={{height:'100%'}}>
                 <div className="row">
                     <div className="col-sm-12">
                     <h6>Dictionary</h6>
                     <div className="row" style={{marginBottom:'10px'}}>
                         <div className="col-sm-3">
                     <input align="center" id={`englishword${this.props.id}`} style={{ marginBottom:'10px',padding:'7px',width:'100%',border:'0px', backgroundColor:'black', color:'white'}}>

                         </input><br/>
               </div>
               <div className="col-sm-3">
                <button style={{border:'1px solid black', borderRadius:'5px'}} onClick={()=>{
                    document.getElementById(`dictloader${this.props.id}`).style.display = 'block'; 
                     document.getElementById(`dictionary${this.props.id}`).style.display='block';

                  axios.get(`https://api.dictionaryapi.dev/api/v1/entries/en/${document.getElementById(`englishword${this.props.id}`).value}`)
                    .then(response => {

                        this.setState({dictioanryRes:response});
                        console.log(this.state.dictioanryRes);
                        this.setState({showDicRes:true});
                    })
                    .catch(err => {
                        console.log(err);
                    });
                }}>Search</button>
                </div>

                <div className="col-sm-6"></div>
                </div>
                <div className="row" align="left" id={`dictionary${this.props.id}`} style={{zIndex:'999999',backgroundColor:'white',position:'absolute',padding:'3px',width:'100%', border:'1px solid black', borderRadius:'5px', fontSize:'12px',display:'none'}}>
                    <div className="col-sm-12">
                        <div className="row">
                            <div className="col-sm-12" align="center">

                                <div align="left">
                                As a noun:
                                {/*dictionary laoder animation*/}<span align="right" style={{
                                display:'none',

                                border: '3px solid #f3f3f3', /* Light grey */
                                borderTop:' 3px solid #0c0c0c', /* Blue */
                                borderRadius: '50%',
                                width: '25px',
                                height: '25px',
                                animation:'spin 2s linear infinite',


                            }} id={`dictloader${this.props.id}`}></span>
                                {   
                                    this.state.showDicRes == true ?
                                    this.state.dictioanryRes.data[0].meaning.noun ?
                                    this.state.dictioanryRes.data[0].meaning.noun.map((noun, index)=>{
                                    document.getElementById(`dictloader${this.props.id}`).style.display = 'none'; 

                                    return <div style={{fontSize:'12px'}}><span style={{color:'rgb(52, 201, 52)'}}>{index+1} </span>{noun.definition}<br/></div>
                                }):<div>It is not a noun</div>:<div></div>}
                             </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-12">
                                As a verb
                                {   
                                    this.state.showDicRes == true ?
                                    this.state.dictioanryRes.data[0].meaning.verb ?
                                    this.state.dictioanryRes.data[0].meaning.verb.map((verb, index)=>{
                                    document.getElementById(`dictloader${this.props.id}`).style.display = 'none'; 

                                    return <div style={{fontSize:'12px'}}><span style={{color:'rgb(52, 201, 52)'}}>{index+1} </span>{verb.definition}<br/></div>
                                }):<div>It is not a verb</div>:<div>q</div>}
                            </div>
                        </div>
                    </div>
                    <button style={{border:'1px solid black'}} onClick={()=>{
                        document.getElementById(`dictionary${this.props.id}`).style.display='none';
                        this.setState({showDicRes:false});
                    }}> Close dictionary</button>
                    </div>
                     </div>
                 </div>

                  <div className="row" style={{height:'70%'}}>   
                <div className="col-sm-3" id={`view${this.props.id}`}>
<br/>            






                </div>



                <div className="col-sm-9" id="notes">
                <h6>Make Notes</h6>
                    <textarea id="notetextarea" style={{padding:'10px' , fontSize:'13px',backgroundColor:'black', width:'100%', color:'white',height:'100%'}} 
                    onChange={()=>{
                        //save notes in the data base
                    }}
                    >{this.props.id}</textarea>

                </div>

                </div>

            </div>
        )
    }
}

export default ReadRoom;
