import React from 'react'
import './YoutubeAudio.css'
import axios from 'axios'
import SearchItem from './SearchItem';

class YoutubeAudio extends React.Component {

    state = {
        searchResults:[],
        showResults:false,
    }

    render(){
        return(
               <div className="container-fluid" id="yt" style={{padding:'10px', paddingTop:'30px'}}>

                   <div className="row">
                        <div className="col-sm-10"><input id="searchbox" placeholder="Search audiobooks" style={{
                            border:'1px solid black',
                            backgroundColor:'black',
                            padding:'5px',
                            width:'100%',
                            borderRadius:'5px',
                            paddingLeft:'10px',
                            color:'white'}}></input>
                        </div>
                   <div className="col-sm-2">
                       <button onClick={()=>{
                           console.log(document.getElementById('searchbox').value);
                        axios.get(`https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=30&key=AIzaSyDbFRSu_mnSfK3sUBm5nBrNx09jdxjyiTo&type=video&q=${document.getElementById('searchbox').value +" audiobook"}`, {}).
                        then((res)=>{
                            this.setState({searchResults:res.data.items});
                            console.log(this.state.searchResults);
                            this.setState({showResults:true});
                        }).catch(()=>{

                        })
                       }}>Search</button>
                   </div>
                   </div>


               <div className="row" style={{height:'100px'}}>
                <div className="col-sm-2"></div>
                <div className="col-sm-5">Title</div>
                <div className="col-sm-3">Uploaded By</div>
                <div className="col-sm-1"></div>
                <div className="col-sm-1"><button>Play</button></div>
               </div>

                   <div className="row">
                        {
                            this.state.showResults == true ? 
                            <div>
                                {
                                    this.state.searchResults.map((item)=>{
                                    return <SearchItem title={item.snippet.title} cover={item.snippet.thumbnail.medium}/>
                                })
                                }
                            </div>:<div></div>
                        }
                   </div>
               </div>

        )
    }

}
export default YoutubeAudio;
