import React from 'react'
import './YoutubeAudio.css'
import axios from 'axios'
import SearchItem from './SearchItem';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch,
  } from 'react-router-dom'
import ReadRoom from './ReadRoom';


class YoutubeAudio extends React.Component {

    state = {
        searchResults:[],
        showResults:false,
        startLoading:false,
        currentAudioTitle:'Audio',
        currentAudioId:'id',

    }



    render(){
        return(



                   <Router className="row" id="yt" style={{padding:'10px', paddingTop:'30px'}}>
                        <div className="col-sm-9"><input id="searchbox" placeholder="Search audiobooks" style={{
                            border:'1px solid black',
                            backgroundColor:'black',
                            padding:'5px',
                            width:'100%',
                            borderRadius:'5px',
                            paddingLeft:'10px',
                            color:'white'}}></input>
                        </div>
                    <div className="col-sm-1" align="center"><span align="center" style={{display:'none'}} id="searchloader"></span></div>
                   <div className="col-sm-2">
                       <Link to="/playfield/search">
                           <button onClick={(e)=>{
                          document.getElementById('searchloader').style.display = 'block'; 
                           var key1 = 'AIzaSyAlMPKiEVfFTzM5USlPOBWGALrKq8n7PFg';
                           var key2 = 'AIzaSyDbFRSu_mnSfK3sUBm5nBrNx09jdxjyiTo';
                           var key3 = 'AIzaSyBAqZUlLZhjd6-XsAC_G8VZl8CMjlUg5EQ';
                           //you can make a key per project on google
                        axios.get(`https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=30&key=${key1}&type=video&q=${document.getElementById('searchbox').value +" audiobook"}`, {}).
                        then((res)=>{
                            this.setState({searchResults:res.data.items});
                            console.log(this.state.searchResults);
                            this.setState({showResults:true});

                        }).catch(()=>{

                        })
                       }}>Search</button>
                       </Link>
                   </div>

                   <br/> 

                   <Switch>
                       <Route path="/playfield/readroom">
                            <ReadRoom title={"vf"} cover={"ff"} id={"re"}/>
                       </Route>
                       <Route path="/playfield/search">





                        {
                            this.state.showResults == true ? 
                            <div>
                                {
                                    this.state.searchResults.map((item)=>{
                                    document.getElementById('searchloader').style.display = 'none';
                                    return  <Link style={{textDecoration:'none', color:'black'}} to="/playfield/readroom`">
                                        <SearchItem title={item.snippet.title} 
                                    cover={item.snippet.thumbnails.default.url} 
                                    uploadedBy={item.snippet.channelTitle}></SearchItem>
                                    </Link>
                                })
                                }
                            </div>:<div id="pw">Previously watched</div>
                        }
                        </Route>
                        </Switch>

               </Router>

        )
    }

}
export default YoutubeAudio;
