import React, { Component } from 'react'
import './ReadRoom.css'
import axios from 'axios'

class ReadRoom extends React.Component {
    render(){
        return(
            <div className='row' style={{height:'100%'}}>



                <div className="col-sm-3" align="center" id={`view${this.props.id}`}>
<br/>            






                </div>

                <div className="col-sm-2" align="center"><h6>Dictionary</h6><input align="center" id="englishword" style={{ marginBottom:'10px',padding:'7px',width:'100%',border:'0px', backgroundColor:'black', color:'white'}}></input><br/>
                <button style={{border:'1px solid black', borderRadius:'5px'}} onClick={()=>{
                    fetch(`https://mashape-community-urban-dictionary.p.rapidapi.com/define?term=${document.getElementById('englishword').value}`, {
                        "method": "GET",
                        "headers": {
                            "x-rapidapi-host": "mashape-community-urban-dictionary.p.rapidapi.com",
                            "x-rapidapi-key": "44cb4970d0msh05b19aa2f687a76p1f1f9cjsn0b9c2331a802"
                        }
                    })
                    .then(response => {
                        console.log(response.body);
                    })
                    .catch(err => {
                        console.log(err);
                    });
                }}>Search</button><br/>
                <div align="left" style={{ padding:'3px',width:'100%', border:'1px solid black', borderRadius:'5px', fontSize:'11px'}}>Search results</div>
                </div>

                <div className="col-sm-7" align="center" id="notes">
                <h6>Make Notes</h6>
                    <textarea id="notetextarea" style={{padding:'10px' , fontSize:'13px',backgroundColor:'black', width:'100%', color:'white',height:'80%'}} 
                    onChange={()=>{
                        //save notes in the data base
                    }}
                    >{this.props.id}</textarea>

                </div>



            </div>
        )
    }
}

export default ReadRoom;
