import React from 'react'
import './SearchItem.css'
import axios from 'axios'
import YoutubeAudio from './YoutubeAudio'
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch,
  } from 'react-router-dom'
import ReadRoom from './ReadRoom';

class SearchItem extends React.Component {


    render(){
        {/* 
        props:
        cover,
        title,
        uploadedBy(channel),
    */}
        return(
            <a style={{textDecoration:'none', color:'black'}} id={this.props.id} className="container" href={`#${this.props.cover}`}>
            <div className="row" onClick={()=>{
               document.getElementById(`${this.props.cover}`).style.display = 'block';
               document.getElementById(`view${this.props.id}`).innerHTML= "/n"+
               "<iframe id={`embed${this.props.id}`} style={{width:'100%'}} /n"+
                "src={`https://www.youtube.com/embed/${this.props.id}?autoplay=1&vq=small`} /n"+
                "frameborder='0' /n"+
                "allow='accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture' /n"+
                "allowfullscreen></iframe>/n"+
                "<div className='row'> /n"+
                    "<div className='col-sm-3'><button>| |</button></div> /n"+
                    "<div className='col-sm-3'><button>X</button></div> /n"+
                    "<div className='col-sm-6'><button>volume</button></div> /n"+
                "</div> /n"

            }} id="searchitem" style={{borderRadius:'5px', border: '0px inset gold', padding:'10px', marginBottom:'15px'}}>
                <div className="col-sm-2"><img src={this.props.cover} style={{width:'60%', border:'1px solid black', borderRadius:'5px',padding:'5px'}}></img></div>
                <div className="col-sm-6"><span style={{color:'rgb(52, 201, 52)'}}>Title:  </span>{this.props.title}</div>
                <div className="col-sm-1"></div>
                <div className="col-sm-3"><span style={{color:'rgb(52, 201, 52)'}}>From: </span>{this.props.uploadedBy}</div>
            </div>

            <div id={`${this.props.cover}`} style={{display:'none'}}>
                <a href={`#${this.props.id}`}><button id="closebook" style={{border:'0px'}} onClick={()=>{
                    document.getElementById(`view${this.props.id}`).innerHTML = 'Loading ...';
                    document.getElementById(`${this.props.cover}`).style.display = 'none';

                }}>Close book</button></a>
            <ReadRoom cover={this.props.cover} title={this.props.title} id={this.props.id} />
            </div>

               </a>


        )
    }

}
export default SearchItem;
