import './ReadRoom.css'
class ReadRoom extends React.Component {
    render(){
        return(
            <div className='row' style={{height:'100vh', padding:'10px', border:'1px solid black'}}>
                  <a href={`#${this.props.id}`}><button id="closebook" style={{border:'0px'}} onClick={()=>{
                    document.getElementById(`view${this.props.id}`).innerHTML = 'Loading ...';
                    document.getElementById(`${this.props.cover}`).style.display = 'none';

                }}>Close this audiobook</button></a>


                <div className="col-sm-3" id={`view${this.props.id}`}>
<br/>            






                </div>


                <div className="col-sm-9" id="notes">

                    <textarea id="notetextarea" style={{padding:'10px' , fontSize:'13px',backgroundColor:'black', width:'100%', color:'white',height:'80%'}} 
                    onChange={()=>{
                        //save notes in the data base
                    }}
                    >{this.props.id}</textarea>

                </div>


            </div>
        )
    }
}

export default ReadRoom;
